import React, { useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import gsap from 'gsap';
import '../assets/css/Navigation.css';
import changeMenuSound from '../assets/sounds/changemenu.mp3';

const Navigation = ({ menuOpen, toggleMenu }) => {
  const circleRef = useRef(null);
  const navRef = useRef(null);
  const navigate = useNavigate();
  const audioRef = useRef(new Audio(changeMenuSound));
  const prevSecRef = useRef(null);
  const menuItems = [
    { title: 'Home', subtitle: 'back to main page', path: '/' },
    { title: 'Contact', subtitle: 'get in touch with us', path: '/contact' },
    { title: 'About Us', subtitle: 'learn more about us', path: '/about' },
    { title: 'Sign In', subtitle: 'access your account', path: '/signin' }
  ];

  useEffect(() => {
    if (menuOpen) {
      // Match the original animation sequence
      gsap.to(".menu-bg", {
        width: "100%",
        height: "100%",
        margin: "0",
        borderRadius: "0",
      });

      gsap.from(circleRef.current, {
        rotate: "90",
        duration: 1,
      });

      // Show the nav after a short delay, exactly like the original
      setTimeout(() => {
        if (navRef.current) {
          navRef.current.style.visibility = "visible";
        }
      }, 500);
    } else {
      // Hide the nav immediately when closing
      if (navRef.current) {
        navRef.current.style.visibility = "hidden";
      }

      // Animate the menu-bg back to its original state
      gsap.to(".menu-bg", {
        width: "20px",
        height: "20px",
        margin: "3em 5em",
        borderRadius: "40em",
      });
    }
  }, [menuOpen]);

  const handleWheel = (event) => {
    if (!menuOpen) return;

    const deltaY = event.deltaY;
    const circle = circleRef.current;

    // Increased sensitivity by using a larger rotation angle (15 instead of 10)
    // and reduced duration for faster response
    if (deltaY > 0) {
      // Scrolling downwards
      gsap.to(circle, {
        rotate: "-=15",
        duration: 0.3,
        ease: "power2.out"
      });

      audioRef.current.play();
    } else if (deltaY < 0) {
      // Scrolling upwards
      gsap.to(circle, {
        rotate: "+=15",
        duration: 0.3,
        ease: "power2.out"
      });

      audioRef.current.play();
    }
  };

  useEffect(() => {
    window.addEventListener('wheel', handleWheel);
    return () => {
      window.removeEventListener('wheel', handleWheel);
    };
  }, [menuOpen]);

  const handleMenuItemClick = (path) => {
    toggleMenu();
    setTimeout(() => {
      navigate(path);
    }, 500);
  };

  const focusText = (index) => {
    const stripes = circleRef.current?.children;
    if (!stripes) return;

    if (prevSecRef.current) {
      // Smooth transition for deselecting previous item
      gsap.to(prevSecRef.current, {
        color: "#bababa",
        duration: 0.3,
        ease: "power2.out"
      });
      prevSecRef.current.classList.remove("active");
    }

    const sec = stripes[index]?.querySelector('.sec');
    if (sec) {
      // Smooth transition for selecting new item
      gsap.to(sec, {
        color: "#0a0a0a",
        duration: 0.3,
        ease: "power2.out"
      });

      setTimeout(() => {
        sec.classList.add("active");
        prevSecRef.current = sec;
      }, 100);
    }
  };

  return (
    <>
      <nav className="main-nav">
        <div className="logo">Hypelabs</div>
        <div className="explore">
          <button className="button button--bestia" onClick={toggleMenu}>
            <div className="button__bg"></div>
            <span>{menuOpen ? "Close" : "Explore"}</span>
          </button>
        </div>
      </nav>

      <div className="nav" ref={navRef}>
        <nav>
          <div className="logo">Hypelabs</div>
          <div className="explore">
            <button className="button button--bestia" onClick={toggleMenu}>
              <div className={`button__bg ${menuOpen ? 'close' : ''}`}></div>
              <span id="navclose">Close</span>
            </button>
          </div>
        </nav>
        <div id="circle" ref={circleRef}>
          {menuItems.map((item, index) => (
            <div 
              key={index} 
              className={`stripe st${index + 1}`}
              onClick={() => handleMenuItemClick(item.path)}
              onMouseEnter={() => focusText(index)}
            >
              <div className="first">
                <div className="smcircle"></div>
              </div>
              <div className="sec">
                <h4>{item.title}</h4>
                <h6>{item.subtitle}</h6>
              </div>
            </div>
          ))}
        </div>
      </div>
    </>
  );
};

export default Navigation;
